import React, {Component,Fragment} from 'react';
import {Col, Container, Row} from "react-bootstrap";
import '../../assets/css/bootstrap.min.css'
import '../../assets/css/menu.css'
import {NavLink} from 'react-router-dom'
class Menu extends Component {
    constructor(props) {
        super();
        this.state={
            colorChange: 'main-menu',
            pageTitle: props.pageTitle,
        }
    }

    onscroll = () =>{
        if(window.scrollY>100){
            this.setState({colorChange:'main-menu-2'})
        }
        else if(window.scrollY<100){
            this.setState({colorChange:'main-menu'})
        }
    }
    componentDidMount() {
        window.addEventListener('scroll',this.onscroll)
    }


    render() {
        return (
            <Fragment>
                <title>{this.state.pageTitle}</title>
                <Container>
                    <Row>
                        <Col>
                            <div className={this.state.colorChange}>
                                <ul>
                                    <li><NavLink exact activeStyle={{color:'green'}} to="/">Home</NavLink></li>
                                    <li><NavLink exact activeStyle={{color:'green'}} to="/service">services</NavLink></li>
                                    <li><NavLink exact activeStyle={{color:'green'}} to="/courses">courses</NavLink></li>
                                    <li><NavLink exact activeStyle={{color:'green'}} to="/portfolio">portfolio</NavLink></li>
                                    <li><NavLink exact activeStyle={{color:'green'}} to="/contact">contact</NavLink></li>
                                    <li><NavLink exact activeStyle={{color:'green'}} to="/about">about</NavLink></li>
                                </ul>
                            </div>
                        </Col>
                    </Row>
                </Container>
            </Fragment>
        );
    }
}

export default Menu;
