import React, {Component,Fragment} from 'react';
import BootstrapLogo from '../../assets/images/bootstrap.png'
import CodeLogo from '../../assets/images/code.png'
import DocumentLogo from '../../assets/images/document.png'
import '../../assets/css/bootstrap.min.css'
import '../../assets/css/Service.css'

import {Col, Container, Row} from "react-bootstrap";

class Service extends Component {
    constructor() {
        super();
        this.state={
            color1: 's1colorBefore'
        }
    }

    onscroll2 = () =>{
        if(window.scrollY>500){
            this.setState({color1: 's1colorBefore'})
        }else if(window.scrollY>200){
            this.setState({color1: 's1colorAfter'})
        }
    }
    componentDidMount(){
        window.addEventListener('scroll',this.onscroll2)
    }
    render() {
        return (
            <Fragment>
                <div className='main_service'>
                    <Container>
                        <div className='section_title'>
                            <h2>my services</h2>
                        </div>
                        <Row>
                            <Col>
                                <div className='service_card'>
                                    <div className='service_icon'>
                                        <img src={BootstrapLogo} alt={'bootstrapLogo'}/>
                                    </div>
                                    <div className='service_content'>
                                        <h3 className={this.state.color1}>web development</h3>
                                        <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book</p>
                                    </div>
                                </div>
                            </Col>
                            <Col>
                                <div className='service_card'>
                                    <div className='service_icon'>
                                        <img src={CodeLogo} alt={'codelogo'}/>
                                    </div>
                                    <div className='service_content'>
                                        <h3 className={this.state.color1}>web development</h3>
                                        <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book</p>
                                    </div>
                                </div>
                            </Col>
                            <Col>
                                <div className='service_card'>
                                    <div className='service_icon'>
                                        <img src={DocumentLogo} alt={'documentlogo'}/>
                                    </div>
                                    <div className='service_content'>
                                        <h3 className={this.state.color1}>web development</h3>
                                        <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book</p>
                                    </div>
                                </div>
                            </Col>
                        </Row>
                    </Container>
                </div>
            </Fragment>
        );
    }
}

export default Service;
